$(function () {
	$("[gid='gaq']").click(function () {
		var c = $(this).attr("gcat");
		var a = $(this).attr("gact");
		var l = $(this).attr("glab");
		var v = $(this).attr("gval");
		if (c == null | c == undefined) {
			c = "未知类别";
		}
		if (a == null | a == undefined) {
			a = "未知操作";
		}
		if (l == null | l == undefined) {
			l = "未知标签";
		}
		if (v == null | v == undefined) {
			v = 1;
		}
		_gaq.push(['_trackEvent', c, a, l, v]);
	});
});

