function beforeLooyuPanelBuilder() {
	this.defUI();
	var closer = document.createElement('a');
	closer.href = "#";
	closer.innerHTML = ' ';
	closer.style.display = 'block';
	closer.style.margin = '3px';
	closer.style.marginRight = '10px'
	closer.style.width = '16px';
	closer.style.height = '16px';
	this.obj.insertBefore(closer, this.obj.firstChild);
	if (typeof closer.style.cssFloat != 'undefined')
		closer.style.cssFloat = 'right';
	else
		closer.style.styleFloat = 'right';
	var me = this;
	closer.onclick = function () {
		me.close();
		return false;
	}
}

script:
	$(function () {
		$(".class_con").mouseover(function () {
			$(this).addClass('class_mask');
		})
		$(".class_con").mouseleave(function () {
			$(this).removeClass('class_mask');
		});
	});
	$(function () {
		$("#divTopReg > .tab_cont").each(function () {
			if ($.trim($(this).html()) == '') {
				$(this).remove();
				$("#" + "tabTopRegCategory" + $(this).attr("id").replace('divTopRegClass', "")).remove();
			}
		});
		$("#divUpcoming > .tab_cont").each(function () {
			if ($.trim($(this).html()) == '') {
				$(this).remove();
				$("#" + "tabUpcomingCategory" + $(this).attr("id").replace('divUpcomingClass', "")).remove();
			}
		});
		$("#divTopReg > .tab_cont:first").addClass("yes");
		$("#tabTopReg > li:first").addClass("mouse_on");
		$("#divUpcoming > .tab_cont:first").addClass("yes");
		$("#tabUpcoming > li:first").addClass("mouse_on");

		$("#tabTopReg > li > a").click(function (e) {
			if (e.target == this) {
				var tabs = $("#tabTopReg > li ");
				var panels = $("#divTopReg >.tab_cont");
				var select = $(this).parent().attr("id");
				var interval = function () {
					if (select != null) {
						tabs.removeClass("mouse_on");
						$("#" + select).addClass("mouse_on");
						panels.removeClass("yes");
						$("#" + "divTopRegClass" + select.replace('tabTopRegCategory', "")).addClass("yes");
					}
				};
				setTimeout(interval, 100);
			}
		});
		$("#tabUpcoming > li > a").click(function(e) {
			if (e.target == this) {
				var tabs = $("#tabUpcoming > li ");
				var panels = $("#divUpcoming >.tab_cont");
				var select = $(this).parent().attr("id");
				var interval = function() {
					if (select != null) {
						tabs.removeClass("mouse_on");
						$("#" + select).addClass("mouse_on");
						panels.removeClass("yes");
						$("#" + "divUpcomingClass" + select.replace('tabUpcomingCategory', "")).addClass("yes");
					}
				};
				setTimeout(interval, 100);
			}
		});
	});
